package Basics;

public class Swap2NumWith3rdVaraible {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=5;
		int y=6;
		int temp;
		
		System.out.println("before swap : "+ x + " "+y);
		
		 temp = x;
		 x = y;
		 y = temp;
		
		
		System.out.println("after swap : "+ x + " "+y);
		
	}

}
